contracts/AgreementNFT.sol.content:
pragma solidity ^0.8.0;

import "erc721a/contracts/ERC721A.sol";
import "./superfluid/interfaces/superfluid/ISuperfluidToken.sol";
import "base64-sol/base64.sol";

contract AgreementNFT is ERC721A {
  constructor() ERC721A("AgreementPosition", "AGP") {}

  struct PositionData {
    ISuperfluidToken token;
    address recipient;
    address sender;
    string description;
    string subscriptionType;
    uint256 flowRate;
    uint256 createdAt;
    bool isActive;
  }

  mapping (uint256 => PositionData) private _positionsById;

  function mint(
    address sender,
    address recipient,
    string memory description,
    string memory subscriptionType,
    uint256 flowRate,
    ISuperfluidToken token
  ) external payable {
    // _safeMint's second argument now takes in a quantity, not a tokenId.
    uint256 currentId = totalSupply();
    _safeMint(msg.sender, 1);

    _positionsById[currentId] = PositionData({
      sender: sender,
      recipient: recipient,
      description: description,
      subscriptionType: subscriptionType,
      flowRate: flowRate,
      token: token,
      isActive: true,
      createdAt: block.timestamp
    });
  }

  function tokenURI(uint256 tokenId) public view override returns (string memory) {
    require(_exists(tokenId), "AgreementPosition: Possition does not exist");

    PositionData memory currentPosition = _positionsById[tokenId];

    (int256 senderBalance, , ,) = currentPosition.token.realtimeBalanceOfNow(currentPosition.sender);

    string memory svg = generateSvg(currentPosition.flowRate, currentPosition.description, senderBalance);
    string memory atributes = generateAtributes(currentPosition.flowRate, currentPosition.description);

    return string(abi.encodePacked(
      'data:application/json;base64,',
      Base64.encode(
        bytes(abi.encodePacked(
          '{',
            '"name":"AgreementPosition #', uint2str(tokenId),'",',
            '"description":"', currentPosition.description, '",',
            '"image_data": "data:image/svg+xml;base64,',svg,'",',
            '"attributes": ', atributes,
          '}'
        ))
      )
    ));
  }

  function generateAtributes(uint256 flowRate, string memory description) private pure returns (string memory){
    bytes memory attributes = abi.encodePacked(
      '[',
        '{"trait_type":"description","value":"',description,'"}',
        '{"trait_type":"flowRate","value":"',flowRate,'"}',
      ']'
    );

    return string(attributes);
  }

  function generateSvg(
    uint256 flowRate,
    string memory description,
    int256 senderBalance
  ) public pure returns (string memory) {
    string memory svg = string(abi.encodePacked(
      '<svg width="800" height="600" xmlns="http://www.w3.org/2000/svg">',
        '<g>',
          '<title>Layer 1</title>',
          '<title>Sender balance: ', senderBalance,'</title>',
          '<title>Flow: ', flowRate,'/sec</title>',
          '<title>description: ', description,'/sec</title>',
        '</g>',
      '</svg>'
    ));

    return svg;
  }

  function uint2str(uint _i) internal pure returns (string memory _uintAsString) {
		if (_i == 0) {
			return "0";
		}
		uint j = _i;
		uint len;

		while (j != 0) {
			len++;
			j /= 10;
		}

		bytes memory bstr = new bytes(len);
		uint k = len;

		while (_i != 0) {
			k = k-1;
			uint8 temp = (48 + uint8(_i - _i / 10 * 10));
			bytes1 b1 = bytes1(temp);
			bstr[k] = b1;
			_i /= 10;
		}

		return string(bstr);
	}
}

