contracts/AccessControl/StakingAccessCon.content:
pragma solidity 0.8.10;

import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";


contract StakingAccessControl is Initializable, AccessControlEnumerableUpgradeable {
  bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
  bytes32 public constant MIGRATOR_ROLE = keccak256("MIGRATOR_ROLE");

  event ManagerAdded(address indexed account);
  event ManagerRemoved(address indexed account);
  event MigratorAdded(address indexed account);
  event MigratorRemoved(address indexed account);

  function __StakingAccessControl_init (address owner) public initializer{
    AccessControlEnumerableUpgradeable.__AccessControlEnumerable_init();
    _setupRole(DEFAULT_ADMIN_ROLE, owner);
    _setupRole(MANAGER_ROLE, owner);
    _setupRole(MIGRATOR_ROLE, owner);
  }

  modifier onlyAdmin() {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "StakingAccessControl: Only admin role");
    _;
  }

  modifier onlyManager() {
    require(hasRole(MANAGER_ROLE, msg.sender), "StakingAccessControl: Only manager role");
    _;
  }

  modifier onlyMigrator() {
    require(hasRole(MIGRATOR_ROLE, msg.sender), "StakingAccessControl: Only migrator role");
    _;
  }

  /**
    * @dev checks if the given account is a manager
    * @param account The account that will be checked
    */
   function isManager(address account) external view returns (bool) {
     return hasRole(MANAGER_ROLE, account);
   }

   /**
    * @dev Adds a new account to the manager role
    * @param account The account that will have the manager role
    */
   function addManager(address account) external virtual {
     grantRole(MANAGER_ROLE, account);

     emit ManagerAdded(account);
   }

   /**
    * @dev Removes the sender from the list the manager role
    */
   function renounceManager() external {
     renounceRole(MANAGER_ROLE, msg.sender);

     emit ManagerRemoved(msg.sender);
   }

   /**
    * @dev Removes the given account from the manager role, if msg.sender is admin
    * @param manager The account that will have the manager role removed
    */
   function removeManager(address manager) external {
     revokeRole(MANAGER_ROLE, manager);

     emit ManagerRemoved(manager);
   }

   /**
    * @dev checks if the given account is a manager
    * @param account The account that will be checked
    */
   function isMigrator(address account) external view returns (bool) {
     return hasRole(MIGRATOR_ROLE, account);
   }

   /**
    * @dev Adds a new account to the migrator role
    * @param account The account that will have the migrator role
    */
   function addMigrator(address account) external virtual {
     grantRole(MIGRATOR_ROLE, account);

     emit MigratorAdded(account);
   }

   /**
    * @dev Removes the sender from the list the migrator role
    */
   function renounceMigrator() external {
     renounceRole(MIGRATOR_ROLE, msg.sender);

     emit MigratorRemoved(msg.sender);
   }

   /**
    * @dev Removes the given account from the migrator role, if msg.sender is admin
    * @param migrator The account that will have the migrator role removed
    */
   function removeMigrator(address migrator) external {
     revokeRole(MIGRATOR_ROLE, migrator);

     emit MigratorRemoved(migrator);
   }
}

contracts/Staking/RewardVault.sol.content:
pragma solidity 0.8.10;

import "@openzeppelin/contracts-upgradeable/token/ERC20/presets/ERC20PresetMinterPauserUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract RewardVault is Initializable, AccessControlUpgradeable {
  ERC20PresetMinterPauserUpgradeable public rewardToken;

  bytes32 public constant CONTROLLER_ROLE = keccak256("CONTROLLER_ROLE");

  /**
   * Initializes the contract
   */
  function initialize(ERC20PresetMinterPauserUpgradeable rewardToken_) public initializer {
    __AccessControl_init();
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _setupRole(CONTROLLER_ROLE, msg.sender);

    rewardToken = rewardToken_;
  }

  function sendReward(
    address to,
    uint256 amount
  ) public onlyRole(CONTROLLER_ROLE) {
    rewardToken.mint(to, amount);
  }

  function addController(address account) public {
    grantRole(CONTROLLER_ROLE, account);
  }
}

contracts/Staking/StakingLibV2Core.sol.content:
pragma solidity 0.8.10;


import "@openzeppelin/contracts-upgradeable/utils/math/MathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "../NFTRarityRegister/INFTRarityRegister.sol";
import "../TokenHelper.sol";
import "./RewardVault.sol";
import "./StakingTypes.sol";
import "./StakingLibV2Base.sol";

library StakingLibV2Core {
  using SafeERC20Upgradeable for IERC20Upgradeable;

  uint256 private constant IDENTITY_MULTIPLIER = 100;

  function claimTickets(
    StakingTypes.StakeInfo storage stake,
    address ticket
  ) public returns(uint256) {

    if(stake.stakingUnits == 0||
      stake.ticketInfo.ticketsMintingRatioWhenEntered == 0 ||
      stake.ticketInfo.ticketsMintingCooldownPeriodRatioWhenEntered == 0
  ) {
      return 0;
    }

    uint256 enteredAtTs = stake.enteredAtTs;
    uint256 lockedTillTs = stake.lockedTillTs;

    uint256 secondsDelta = MathUpgradeable.min(
      (uint256(block.timestamp) - enteredAtTs),
      (lockedTillTs - enteredAtTs)
    ) + stake.ticketInfo.ticketsMintingCooldownPeriodRatioWhenEntered; // count as passed from day 0
    uint256 periodsPassed = secondsDelta / stake.ticketInfo.ticketsMintingCooldownPeriodRatioWhenEntered;
    // 1. multiply tickets
    uint256 multipliedUnits = stake.stakingUnits * periodsPassed;
    // 1. get printable tickets
    uint256 printableTickets = multipliedUnits / stake.ticketInfo.ticketsMintingRatioWhenEntered;
    // 2. subtract any previously minted
    uint256 netPrintableTickets = printableTickets - stake.ticketInfo.ticketsClaimedForCurrentStake;
    uint256 withPending = netPrintableTickets + stake.ticketInfo.pendingTickets;

    TokenHelper._mintTickets(ticket, msg.sender, withPending);
    stake.ticketInfo.ticketsClaimedForCurrentStake = stake.ticketInfo.ticketsClaimedForCurrentStake + netPrintableTickets;
    stake.ticketInfo.pendingTickets = 0;

    return withPending;
  }

  function addNftToStake(
    StakingTypes.StakeInfo storage stake,
    address tokenAddress,
    uint256 tokenId,
    StakingTypes.PoolInfo storage poolInfo
  ) public {
    uint256 rewardMultiplier = 100;

    require(stake.stakingUnits > 0, 'Staking; No stake found');
    require(rewardMultiplier >= 100, 'Staking: NFT multiplier must be at least 100');
    require(stake.stakedNftAddress == address(0), 'Staking: Stake already has a token');

    bool success = TokenHelper.transferNft(tokenAddress, tokenId, 1, '', msg.sender, address(this));
    require(success, "Staking: could not add NFT to stake");

    addNft(
      stake,
      tokenAddress,
      tokenId,
      poolInfo,
      rewardMultiplier
    );
  }

  function addNft(
    StakingTypes.StakeInfo storage stake,
    address tokenAddress,
    uint256 tokenId,
    StakingTypes.PoolInfo storage poolInfo,
    uint256 nftMultiplier
  ) private {
    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );
    uint256 pending = StakingLibV2Base.getPendingForStake(stake, poolInfo);

    if(pending > 0) {
      poolInfo.rewardVault.sendReward(address(this), pending);

      // If the user got rewards we set the cursor to the current global cursor and the reward debt to 0
      // so that we will not perform the same calculations again for past periods
      stake.cursor = poolInfo.globalPeriodCursor;

      // This will be calculated again below using the new user cursor we just set
      stake.rewardDebt = 0;
      // This was included in the total pending amount
      stake.pendingRewards = 0;
    }

    StakingLibV2Base.updateStake(
      stake,
      poolInfo,
      stake.amount + pending,
      StakingLibV2Base.getMultiplierForLock(stake.currentLockDurationIndex, poolInfo.lockPeriodMultipliers),
      nftMultiplier,
      stake.currentLockDurationIndex, // we keep this value the same
      stake.lockedTillTs // we keep this value the same
    );
    stake.stakedNftAddress = tokenAddress;
    stake.stakedNftId = tokenId;
  }

  function withdraw(
    StakingTypes.StakeInfo storage stake,
    uint256 tokenId,
    StakingTypes.PoolInfo storage poolInfo
  ) public returns (uint256 unstakeAmount) {
    require(stake.lockedTillTs <= block.timestamp, "stake locked");

    address tokenOwner = TokenHelper.ownerOf(address(poolInfo.minepunks), tokenId);

    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );
    StakingLibV2Base.releasePending(
      stake,
      tokenOwner,
      poolInfo
    );

    unstakeAmount = stake.amount;
    // poolInfo.token.safeTransfer(address(msg.sender), stake.amount);

    StakingLibV2Base.updateStakeDebt(
      stake,
      poolInfo.rewardPeriods
    );

    // poolInfo.totalStakingUnits -= stake.stakingUnits;
    // stake.amount = 0;
    // stake.stakingUnits = 0;

    if(stake.stakedNftAddress != address(0)) {
      bool success = TokenHelper.transferNft(stake.stakedNftAddress, stake.stakedNftId, 1, '', address(this), msg.sender);
      require(success, "Staking: could not withdraw NFT");
    }
  }

  function unstakeNft(StakingTypes.StakeInfo storage stake) private {
    bool success = TokenHelper.transferNft(stake.stakedNftAddress, stake.stakedNftId, 1, '', address(this), msg.sender);
    require(success, "Staking: could not withdraw NFT");

    stake.stakedNftId = 0;
    stake.stakedNftAddress = address(0);
  }

  function moveStakedNft(
    StakingTypes.PoolInfo storage poolInfo,
    StakingTypes.StakeInfo storage stake,
    StakingTypes.StakeInfo storage stakeNext
  ) private {
    uint256 multipler = 100;
    StakingLibV2Base.updateStakeStakingUnits(stakeNext, StakingLibV2Base.applyPercent(stakeNext.stakingUnits, multipler), poolInfo);
    stakeNext.stakedNftAddress = stake.stakedNftAddress;
    stakeNext.stakedNftId = stake.stakedNftId;
    stake.stakedNftId = 0;
    stake.stakedNftAddress = address(0);
  }

  function handleNftRestake(
    StakingTypes.PoolInfo storage poolInfo,
    StakingTypes.StakeInfo storage stake,
    StakingTypes.StakeInfo storage stakeNext,
    address tokenAddress,
    uint256 tokenId,
    bool removeNftFromStake
  ) private {
    if(removeNftFromStake) {
      unstakeNft(stake);
    }
    else if(stake.stakedNftAddress != address(0) && tokenAddress == address(0) && tokenId == 0) {
      moveStakedNft(poolInfo, stake, stakeNext);
    }
    else if(stake.stakedNftAddress != address(0) && tokenAddress != address(0) && tokenId != 0) {
      uint256 multipler = 100;
      StakingLibV2Base.updateStakeStakingUnits(stakeNext, StakingLibV2Base.applyPercent(stakeNext.stakingUnits, multipler), poolInfo);
      stakeNext.stakedNftAddress = tokenAddress;
      stakeNext.stakedNftId = tokenId;
      bool success = TokenHelper.transferNft(tokenAddress, tokenId, 1, '', msg.sender, address(this));
      require(success, "Staking: could not stake NFT");

      unstakeNft(stake);
    }
    else if(stake.stakedNftAddress == address(0) && tokenAddress != address(0) && tokenId != 0) {
      uint256 multipler = 100;
      StakingLibV2Base.updateStakeStakingUnits(stakeNext, StakingLibV2Base.applyPercent(stakeNext.stakingUnits, multipler), poolInfo);
      stakeNext.stakedNftAddress = tokenAddress;
      stakeNext.stakedNftId = tokenId;
    }
  }

  function updateNewStakeData(
    StakingTypes.PoolInfo storage poolInfo,
    StakingTypes.StakeInfo storage stakeNext,
    uint256 amount,
    uint256 lockIndex,
    uint256 lockMultiplier,
    uint256 lockedTillTs
  ) public {
    stakeNext.amount = amount;
    StakingLibV2Base.updateStakeStakingUnits(stakeNext, StakingLibV2Base.applyPercent(stakeNext.amount, lockMultiplier), poolInfo);
    stakeNext.currentLockDurationIndex = lockIndex;
    stakeNext.lockedTillTs = lockedTillTs;
    stakeNext.enteredAtTs = block.timestamp;
  }

  function reStake(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.StakeInfo storage stakeNext,
    StakingTypes.PoolInfo storage poolInfo,
    uint256 amount,
    uint256 lockIndex,
    address tokenAddress,
    uint256 tokenId,
    bool removeNftFromStake
  ) public {
    uint256 lockMultiplier = StakingLibV2Base.getMultiplierForLock(lockIndex, poolInfo.lockPeriodMultipliers);
    uint256 lockDurationSeconds = StakingLibV2Base.getLockDurationInSeconds(lockIndex, poolInfo.lockPeriods);
    uint256 lockedTillTs = block.timestamp + lockDurationSeconds;
    require(stake.lockedTillTs <= block.timestamp, 'stake locked');
    require(stake.amount > 0, 'cannot restake without an existing stake');
    require(amount > 0, 'cannot restake 0');

    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );

    uint256 pending;
    if (stake.stakingUnits > 0) {
      pending = StakingLibV2Base.getPendingForStake(stake, poolInfo);

      if(pending > 0) {
        // move the rewards into the contract until we calculate the final restake amount
        poolInfo.rewardVault.sendReward(address(this), pending);

        // If the stake got rewards we set the cursor to the current global cursor and the reward debt to 0
        // so that we will not perform the same calculations again for past periods
        stake.cursor = poolInfo.globalPeriodCursor;

        // This will be calculated again below using the new stake cursor we just set
        stake.rewardDebt = 0;
        // This was included in the total pending amount
        stake.pendingRewards = 0;
      }
    }
    uint256 maxRestakeAmount = stake.amount + pending;
    require(amount <= maxRestakeAmount, 'cannot restake more than the staked amount plus pending rewards');

    uint256 remainder = maxRestakeAmount - amount;
    if(remainder > 0) {
      poolInfo.token.safeTransfer(msg.sender, remainder);
    }

    poolInfo.totalStakingUnits -= stake.stakingUnits;
    stake.stakingUnits = 0;
    stake.amount = 0;

    updateNewStakeData(
      poolInfo,
      stakeNext,
      amount,
      lockIndex,
      lockMultiplier,
      lockedTillTs
    );

    handleNftRestake(
      poolInfo,
      stake,
      stakeNext,
      tokenAddress,
      tokenId,
      removeNftFromStake
    );

    StakingLibV2Base.updateStakeDebt(stake, poolInfo.rewardPeriods);
  }

  function _compounRewards(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.PoolInfo storage poolInfo
  ) private returns(uint256 pending) {
    if (stake.stakingUnits > 0) {
      pending = StakingLibV2Base.getPendingForStake(stake, poolInfo);

      if(pending > 0) {
        poolInfo.rewardVault.sendReward(address(this), pending);

        // If the user got rewards we set the cursor to the current global cursor and the reward debt to 0
        // so that we will not perform the same calculations again for past periods
        stake.cursor = poolInfo.globalPeriodCursor;

        // This will be calculated again below using the new user cursor we just set
        stake.rewardDebt = 0;
        // This was included in the total pending amount
        stake.pendingRewards = 0;
      }
    }
  }

  function compound(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.PoolInfo storage poolInfo,
    uint256 newLevel
  ) public returns(uint256 pending) {
    // require(stake.amount > 0, 'cannot compound without stake');
    // require(stake.lockedTillTs > block.timestamp, 'cannot compound unlocked stake');

    uint256 lockMultiplier = 100; // StakingLibV2Base.getMultiplierForLock(stake.currentLockDurationIndex, poolInfo.lockPeriodMultipliers);

    uint256 nftMultiplier = stake.stakedNftAddress != address(0)
      ? 100
      : IDENTITY_MULTIPLIER;

    require(nftMultiplier >= 100, 'Staking: NFT multiplier must be at least 100');

    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );

    pending = _compounRewards(
      stake,
      poolInfo
    );

    StakingLibV2Base.updateStake(
      stake,
      poolInfo,
      newLevel,
      lockMultiplier,
      nftMultiplier,
      stake.currentLockDurationIndex, // we keep this value the same
      stake.lockedTillTs // we keep this value the same
    );
  }

  function deposit(
    uint256 amount,
    uint256 lockIndex,
    StakingTypes.StakeInfo storage stake,
    StakingTypes.PoolInfo storage poolInfo
  ) public returns (uint256) {
    uint256 lockMultiplier = 100; // StakingLibV2Base.getMultiplierForLock(lockIndex, poolInfo.lockPeriodMultipliers);
    uint256 lockDurationTs = 0; // StakingLibV2Base.getLockDurationInSeconds(lockIndex, poolInfo.lockPeriods);

    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );

    stake.enteredAtTs = block.timestamp;

    StakingLibV2Base.updateStake(
      stake,
      poolInfo,
      stake.amount + amount,
      lockMultiplier,
      IDENTITY_MULTIPLIER,
      lockIndex,
      block.timestamp + lockDurationTs
    );

    uint256 printedTickets = 0;

    return printedTickets;
  }

  function emergencyWithdraw(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.PoolInfo memory poolInfo
  ) public {
    poolInfo.token.safeTransfer(address(msg.sender), stake.amount);
    poolInfo.totalStakingUnits -= stake.stakingUnits;
    stake.amount = 0;
    stake.stakingUnits = 0;
    stake.rewardDebt = 0;
    stake.pendingRewards = 0;
  }
}

contracts/Staking/StakingLibV2Base.sol.content:
pragma solidity 0.8.10;


import "@openzeppelin/contracts-upgradeable/utils/math/MathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "../NFTRarityRegister/INFTRarityRegister.sol";
import "./RewardVault.sol";
import "./StakingTypes.sol";
import "../Minepunks.sol";
import "hardhat/console.sol";

library StakingLibV2Base {
  using SafeERC20Upgradeable for IERC20Upgradeable;

  function init(
    IERC20Upgradeable token,
    uint256 _endTs,
    uint256 _ticketsMintingRatio,
    uint256 _ticketsMintingCooldownPeriod,
    uint256[] memory periodsStartTs,
    uint256[] memory periodsRewardsPerSecond,
    RewardVault _rewardVault,
    Minepunks _minepunks,
    StakingTypes.PoolInfo storage poolInfo
  ) public {
    poolInfo.token = token;
    poolInfo.rewardVault = _rewardVault;
    poolInfo.ticketsMintingRatio = _ticketsMintingRatio;
    poolInfo.ticketsMintingCooldownPeriod = _ticketsMintingCooldownPeriod;
    poolInfo.minepunks = _minepunks;

    uint256 endTs;

    for(uint i ; i < periodsStartTs.length ; i++) {
      if(i == periodsStartTs.length - 1) {
        // For the last period the end ts is the streams end ts
        endTs = _endTs;
      }
      else {
        // For every other period the end ts is 1 second before the next period starts
        endTs = periodsStartTs[i + 1] - 1;
      }

      // Push the period in the array of periods for the stream
      poolInfo.rewardPeriods.push(StakingTypes.RewardPeriodInfo({
        startTs: periodsStartTs[i],
        endTs: endTs,
        rewardPerSecond: periodsRewardsPerSecond[i],
        accRewardPerShare: 0,
        lastRewardTs: 0,
        endAccRewardPerShare: 0
      }));
    }
  }

  /**
	* @notice Will parse bytes data to get an uint256
	* @param data bytes data
	* @param from from where to start the parsing
	*/
	function parse32BytesToUint256(bytes memory data, uint256 from) public pure returns (uint256 parsed){
		assembly {parsed := mload(add(add(data, from), 32))}
	}

  /**
	* @notice Will parse bytes data to get an address
	* @param data bytes data
	* @param from from where to start the parsing
	*/
	function parseBytesToAddress(bytes memory data, uint256 from) public pure returns (address parsed){
		assembly {parsed := mload(add(add(data, from), 20))}
	}

  /**
	* @notice Will parse the stake bytes data to get the stake index
	* @dev [(index 32 bytes), (nft address 20 bytes), (tokenId 32 bytes)]
	* @param data bytes from the stake action
	* @return (uint256) the parsed index
	*/
	function getStakeIndexFromCalldata(bytes memory data) public pure returns (uint256) {
		return parse32BytesToUint256(data, 0);
	}

	/**
	* @notice Will parse the stake bytes data to get the NFT address
	* @dev [(index 32 bytes), (nft address 20 bytes), (tokenId 32 bytes)]
	* @param data bytes from the stake action
	* @return (address) the parsed address
	*/
	function getTokenAddressFromCalldata(bytes memory data) public pure returns (address) {
		return parseBytesToAddress(data, 32);
	}

	/**
	* @notice Will parse the stake bytes data to get the NFT tokeId
	* @dev [(index 32 bytes), (nft address 20 bytes), (tokenId 32 bytes)]
	* @param data bytes from the stake action
	* @return (uint256) the parsed tokenId
	*/
	function getTokenIdFromCalldata(bytes memory data) public pure returns (uint256) {
		return parse32BytesToUint256(data, 52);
	}

  function getDepositInfo(bytes memory data) public pure returns(uint256, address, uint256) {
    uint256 lockIndex = getStakeIndexFromCalldata(data);
    address tokenAddress;
    uint256 tokenId;

    if (data.length >= 84) { // [32, 20. 32] == [index, address, tokenId]
      tokenAddress = getTokenAddressFromCalldata(data);
      tokenId = getTokenIdFromCalldata(data);
    }

    return (lockIndex, tokenAddress, tokenId);
  }

  function updatePeriod(
    uint256 startTs,
    uint256 endTs,
    uint256 periodRewardsPerSecond,
    StakingTypes.PoolInfo storage poolInfo
  ) public {
    require(startTs < endTs, "Staking: Start cannot be after the end");
    require(startTs > block.timestamp, "Staking: Cannot edit periods in the past");

    // We set it to this so that we know if a matching period where this can be added was found
    uint256 updatePeriodIndex = poolInfo.rewardPeriods.length;

    for(uint256 i = 0; i < poolInfo.rewardPeriods.length; i++) {
      if(startTs > poolInfo.rewardPeriods[i].startTs && endTs < poolInfo.rewardPeriods[i].endTs) {
        updatePeriodIndex = i;
        break;
      }
    }

    // This means the period did not match any of the criteria in the for loop and cannot be added
    require(updatePeriodIndex < poolInfo.rewardPeriods.length, "Staking: Invalid period");

    StakingTypes.RewardPeriodInfo[] memory tmp = new StakingTypes.RewardPeriodInfo[](poolInfo.rewardPeriods.length + 2);

    // Copy the elements before the period we are changing in the temporary array
    for(uint256 i = 0; i < updatePeriodIndex; i++) {
      tmp[i] = poolInfo.rewardPeriods[i];
    }

    // Set the 3 periods we will create now
    tmp[updatePeriodIndex] = StakingTypes.RewardPeriodInfo({
      startTs: poolInfo.rewardPeriods[updatePeriodIndex].startTs,
      endTs: startTs - 1,
      rewardPerSecond: poolInfo.rewardPeriods[updatePeriodIndex].rewardPerSecond,
      accRewardPerShare: 0,
      lastRewardTs: 0,
      endAccRewardPerShare: 0
    });

    tmp[updatePeriodIndex + 1] = StakingTypes.RewardPeriodInfo({
      startTs: startTs,
      endTs: endTs,
      rewardPerSecond: periodRewardsPerSecond,
      accRewardPerShare: 0,
      lastRewardTs: 0,
      endAccRewardPerShare: 0
    });

    tmp[updatePeriodIndex + 2] = StakingTypes.RewardPeriodInfo({
      startTs: endTs + 1,
      endTs: poolInfo.rewardPeriods[updatePeriodIndex].endTs,
      rewardPerSecond: poolInfo.rewardPeriods[updatePeriodIndex].rewardPerSecond,
      accRewardPerShare: 0,
      lastRewardTs: 0,
      endAccRewardPerShare: 0
    });

    // Copy the elements after the period we are changing in the temporary array
    for(uint256 i = updatePeriodIndex + 1; i < poolInfo.rewardPeriods.length; i++) {
      tmp[i + 2] = poolInfo.rewardPeriods[i];
    }

    // This is to space and will get the correct data after
    poolInfo.rewardPeriods.push(StakingTypes.RewardPeriodInfo({
      startTs: 0,
      endTs: 0,
      rewardPerSecond: 0,
      accRewardPerShare: 0,
      lastRewardTs: 0,
      endAccRewardPerShare: 0
    }));
    poolInfo.rewardPeriods.push(StakingTypes.RewardPeriodInfo({
      startTs: 0,
      endTs: 0,
      rewardPerSecond: 0,
      accRewardPerShare: 0,
      lastRewardTs: 0,
      endAccRewardPerShare: 0
    }));

    // Copy the final periods in the storage
    for(uint256 i = 0; i < tmp.length; i++) {
      poolInfo.rewardPeriods[i] = tmp[i];
    }
  }

  // View function to see pending reward on frontend.
  function pendingReward(
    StakingTypes.StakeInfo memory stake,
    StakingTypes.PoolInfo storage poolInfo
  ) public view returns (uint256) {
    uint256 amount = stake.pendingRewards;

    for (uint256 j = stake.cursor; j < poolInfo.rewardPeriods.length; j++) {
      if(poolInfo.rewardPeriods[j].startTs > block.timestamp) {
        break;
      }

      uint256 accRewardPerShare = poolInfo.rewardPeriods[j].accRewardPerShare;
      uint256 pendingSeconds = getPendingSeconds(poolInfo.rewardPeriods[j], poolInfo.lastRewardTs);
      uint256 pendingRewards = poolInfo.rewardPeriods[j].rewardPerSecond * pendingSeconds;

      accRewardPerShare = accRewardPerShare + (pendingRewards * 1e18 / poolInfo.totalStakingUnits);

      uint256 periodAmount =  stake.stakingUnits * accRewardPerShare / 1e18;
      // console.log("periodAmount: %s", periodAmount);

      amount += periodAmount;
    }

    // console.log("Pending reward: %s", amount);

    // console.log("stake.rewardDebt: %s", stake.rewardDebt);
    return amount - stake.rewardDebt;
  }

  function getPendingForStake(
    StakingTypes.StakeInfo memory stake,
    StakingTypes.PoolInfo memory poolInfo
  ) public view returns(uint256 pending) {
    uint256 amount = stake.pendingRewards;

    for (uint256 j = stake.cursor; j < poolInfo.rewardPeriods.length; j++) {
      if(poolInfo.rewardPeriods[j].startTs > block.timestamp) {
        break;
      }

      uint256 accRewardPerShare = poolInfo.rewardPeriods[j].accRewardPerShare;
      uint256 periodAmount =  stake.stakingUnits * accRewardPerShare / 1e18;

      amount += periodAmount;
    }

    if (stake.rewardDebt > amount) {
      return 0;
    }

    return amount - stake.rewardDebt;
  }

  function getPendingSeconds(
    StakingTypes.RewardPeriodInfo memory period,
    uint256 lastRewardTs
  ) public view returns (uint256) {
    // If the period has not started yet
    if(MathUpgradeable.min(block.timestamp, period.endTs) < MathUpgradeable.max(lastRewardTs, period.startTs)) {
      return 0;
    }

    return MathUpgradeable.min(block.timestamp, period.endTs) - MathUpgradeable.max(lastRewardTs, period.startTs);
  }

  function getMultiplierForLock(uint256 lockIndex, uint256[] memory lockPeriodMultipliers) public pure returns (uint256) {
    require(lockPeriodMultipliers.length > lockIndex, 'Invalid lock index');

    return lockPeriodMultipliers[lockIndex];
  }

  function getLockDurationInSeconds(uint256 lockIndex, uint256[] memory lockPeriods) public pure returns (uint256) {
    require(lockPeriods.length > lockIndex, 'Invalid lock index');

    return lockPeriods[lockIndex];
  }

  function updateStakeDebt(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.RewardPeriodInfo[] memory rewardPeriods
  ) public {
    stake.rewardDebt = 0;

    for (uint256 j = stake.cursor; j < rewardPeriods.length; j++) {
      if(rewardPeriods[j].accRewardPerShare > 0 ){
        uint256 accRewardPerShare = rewardPeriods[j].accRewardPerShare;

        stake.rewardDebt += (stake.stakingUnits * accRewardPerShare / 1e18);
      }
    }
  }

  function updatePool(
    StakingTypes.PoolInfo storage poolInfo
  ) public {
    for (uint256 periodIndex = poolInfo.globalPeriodCursor; periodIndex < poolInfo.rewardPeriods.length; periodIndex++) {
      // If period has not started then ignore it
      if(poolInfo.rewardPeriods[periodIndex].startTs > block.timestamp) continue;

      if (
        // We have not calculated for the current block (useful for multiple transactions in the same block)
        block.timestamp > poolInfo.rewardPeriods[periodIndex].lastRewardTs
      ) {
        if (poolInfo.totalStakingUnits == 0) {
          poolInfo.rewardPeriods[periodIndex].lastRewardTs = block.timestamp;
        } else {
          uint256 accRewardPerShare = poolInfo.rewardPeriods[periodIndex].accRewardPerShare;
          uint256 pendingSeconds = getPendingSeconds(poolInfo.rewardPeriods[periodIndex], poolInfo.lastRewardTs);
          uint256 pendingRewards = poolInfo.rewardPeriods[periodIndex].rewardPerSecond * pendingSeconds;

          poolInfo.rewardPeriods[periodIndex].accRewardPerShare = accRewardPerShare + (pendingRewards * 1e18 / poolInfo.totalStakingUnits);
          poolInfo.rewardPeriods[periodIndex].lastRewardTs = block.timestamp;
        }
      }

      if(
        poolInfo.rewardPeriods[poolInfo.globalPeriodCursor].endTs < block.timestamp
        && poolInfo.rewardPeriods[periodIndex].endTs > block.timestamp
      ) {
        poolInfo.globalPeriodCursor = periodIndex;
      }
    }

    poolInfo.lastRewardTs = block.timestamp;
  }

  function updateStakeCursor(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.PoolInfo storage poolInfo
  ) public {
    if(stake.stakingUnits == 0) {
      stake.cursor = poolInfo.globalPeriodCursor;
    }
  }

  function releasePending(
    StakingTypes.StakeInfo storage stake,
    address userAddress,
    StakingTypes.PoolInfo storage poolInfo
  ) public {
    if (stake.stakingUnits > 0) {
      uint256 pending = getPendingForStake(stake, poolInfo);

      if(pending > 0) {
        poolInfo.rewardVault.sendReward(address(userAddress), pending);

        // If the stake got rewards we set the cursor to the current global cursor and the reward debt to 0
        // so that we will not perform the same calculations again for past periods
        stake.cursor = poolInfo.globalPeriodCursor;

        // This will be calculated again below using the new user cursor we just set
        stake.rewardDebt += pending;
        // This was included in the total pending amount
        stake.pendingRewards = 0;
      }
    }
  }

  function updateStake(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.PoolInfo storage poolInfo,
    uint256 amount,
    uint256 lockMultiplier,
    uint256 nftMultiplier,
    uint256 lockDurationIndex,
    uint256 lockedTillTs
  ) public {
    require(lockMultiplier >= 100, 'Staking: Lock multiplier must be at least 100');
    require(nftMultiplier >= 100, 'Staking: NFT multiplier must be at least 100');

    stake.amount = amount;
    uint256 withLockMultiplier = applyPercent(stake.amount, lockMultiplier);
    uint256 withNftMultiplier = applyPercent(withLockMultiplier, nftMultiplier);

    updateStakeStakingUnits(stake, withNftMultiplier, poolInfo);
    stake.currentLockDurationIndex = lockDurationIndex;
    stake.lockedTillTs = lockedTillTs;
    updateStakeDebt(
      stake,
      poolInfo.rewardPeriods
    );
  }

  function updateStakeStakingUnits(
    StakingTypes.StakeInfo storage stake,
    uint256 newStakingUnits,
    StakingTypes.PoolInfo storage poolInfo
  ) public {
    poolInfo.totalStakingUnits -= stake.stakingUnits;
    stake.stakingUnits = newStakingUnits;
    poolInfo.totalStakingUnits += newStakingUnits;
  }

  function createStakeObject(StakingTypes.PoolInfo memory poolInfo)
    public
    pure
    returns (StakingTypes.StakeInfo memory)
  {
    return StakingTypes.StakeInfo(
      0,
      0,
      0,
      0,
      0,
      0,
      0,
      address(0),
      0,
      0,
      0,
      StakingTypes.TicketInfo(
        poolInfo.ticketsMintingRatio,
        poolInfo.ticketsMintingCooldownPeriod,
        0,
        0
      )
    );
  }

  function createNewStake(
    mapping(uint256 => StakingTypes.StakeInfo) storage stakeInfo,
    StakingTypes.PoolInfo memory poolInfo,
    uint256 forTokenId
  ) public returns(StakingTypes.StakeInfo storage){
    StakingTypes.StakeInfo storage stake = stakeInfo[forTokenId];

    return stake;
  }

  /**
	* @notice Will apply a percentage to a number
	* @param number The number to multiply
	* @param percent The percentage to apply
	* @return (uint256) the operation result
	*/
	function applyPercent(uint256 number, uint256 percent) public pure returns (uint256) {
		return number * percent / 100;
	}

  function addPeriod(StakingTypes.PoolInfo storage poolInfo, uint256 endTs, uint256 periodRewardsPerSecond) public {
    require(endTs > poolInfo.rewardPeriods[poolInfo.rewardPeriods.length - 1].endTs + 1, "Staking: end date cannot be before the latest period end date");
    uint256 periodsStartTs = poolInfo.rewardPeriods[poolInfo.rewardPeriods.length - 1].endTs + 1;

    // Push the period in the array of periods for the stream
    poolInfo.rewardPeriods.push(StakingTypes.RewardPeriodInfo({
      startTs: periodsStartTs,
      endTs: endTs,
      rewardPerSecond: periodRewardsPerSecond,
      accRewardPerShare: 0,
      lastRewardTs: 0,
      endAccRewardPerShare: 0
    }));
  }
