contracts/TokenHelper.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity 0.8.10;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";

import "./Raffle/IRaffleTicket.sol";

library TokenHelper {
	function ERC20Transfer(
		address token,
		address to,
		uint256 amount
	)
		public
	{
		(bool success, bytes memory data) =
				token.call(abi.encodeWithSelector(IERC20.transfer.selector, to, amount));
		require(success && (data.length == 0 || abi.decode(data, (bool))), 'ERC20: transfer amount exceeds balance');
	}

    function ERC20TransferFrom(
			address token,
			address from,
			address to,
			uint256 amount
    )
			public
		{
			(bool success, bytes memory data) =
					token.call(abi.encodeWithSelector(IERC20.transferFrom.selector, from, to, amount));
			require(success && (data.length == 0 || abi.decode(data, (bool))), 'ERC20: transfer amount exceeds balance or allowance');
    }

    function transferFrom(
        address token,
        uint256 tokenId,
        address from,
        address to
    )
            public
            returns (bool)
        {
                (bool success,) = token.call(abi.encodeWithSelector(IERC721.transferFrom.selector, from, to, tokenId));

                // in the ERC721 the transfer doesn't return a bool. So we need to check explicitly.
                return success;
    }

    function ERC155transferFrom(
        address token,
        uint256 tokenId,
        uint256 amount,
        bytes memory data,
        address from,
        address to
    )
            public
            returns (bool)
        {
                (bool success,) = token.call(abi.encodeWithSelector(IERC1155.safeTransferFrom.selector, from, to, tokenId, amount, data));

                // in the ERC1155 the transfer doesn't return a bool. So we need to check explicitly.
                return success;
    }

    function _mintTickets(
        address ticket,
        address to,
        uint256 amount
    ) public {
        (bool success,) = ticket.call(abi.encodeWithSelector(IRaffleTicket.mint.selector, to, 0, amount));

        require(success, 'ERC1155: mint failed');
    }

    function transferNft(
        address token,
        uint256 tokenId,
        uint256 amount,
        bytes memory data,
        address from,
        address to
    ) public returns (bool) {
        bool isERC1155 = IERC1155(token).supportsInterface(type(IERC1155).interfaceId);

        if(isERC1155) {
            return ERC155transferFrom(token, tokenId, amount, data, from, to);
        }
        else {
            return transferFrom(token, tokenId, from, to);
        }
    }

    function ownerOf(
        address tokenAddress,
        uint256 tokenId
    ) public view returns (address) {
        return IERC721(tokenAddress).ownerOf(tokenId);
    }
}

