contracts/AccessControl/RarityRegisterAd.content:
pragma solidity 0.8.10;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";


contract RarityRegisterAdminAccessControl is AccessControlEnumerable {
  bytes32 public constant RARITY_MANAGER_ROLE = keccak256("RARITY_MANAGER_ROLE");

  address public managerOf;

  event RarityRegisterManagerAdded(address indexed account, address managerOf);
  event RarityRegisterManagerRemoved(address indexed account, address managerOf);
  event RarityManagerAdded(address indexed account, address managerOf);
  event RarityManagerRemoved(address indexed account, address managerOf);

  /**
  * @dev Constructor Add the given account both as the main Admin of the smart contract and a checkpoint admin
  * @param owner The account that will be added as owner
  */
  constructor (address owner, address _managerOf) {
    require(
      owner != address(0),
      "Owner should be a valid address"
    );

    managerOf = _managerOf;

    _setupRole(DEFAULT_ADMIN_ROLE, owner);
    _setupRole(RARITY_MANAGER_ROLE, owner);
  }

  modifier onlyAdmin() {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "RarityRegisterAdminAccessControl: Only admin role");
    _;
  }

   modifier onlyRarityManager() {
     require(hasRole(RARITY_MANAGER_ROLE, msg.sender), "RarityRegisterAdminAccessControl: Only rarity manager role");
     _;
   }

  /**
    * @dev checks if the given account is a prizeManager
    * @param account The account that will be checked
    */
   function isRarityManager(address account) external view returns (bool) {
     return hasRole(RARITY_MANAGER_ROLE, account);
   }

   /**
    * @dev Adds a new account to the prizeManager role
    * @param account The account that will have the prizeManager role
    */
   function addRarityManager(address account) external onlyAdmin virtual {
     grantRole(RARITY_MANAGER_ROLE, account);

     emit RarityManagerAdded(account, managerOf);
   }

   /**
    * @dev Removes the sender from the list the prizeManager role
    */
   function renounceRarityManager() external {
     renounceRole(RARITY_MANAGER_ROLE, msg.sender);

     emit RarityManagerRemoved(msg.sender, managerOf);
   }

   /**
    * @dev Removes the given account from the prizeManager role, if msg.sender is admin
    * @param prizeManager The account that will have the prizeManager role removed
    */
   function removeRarityManager(address prizeManager) onlyAdmin external {
     revokeRole(RARITY_MANAGER_ROLE, prizeManager);

     emit RarityManagerRemoved(prizeManager, managerOf);
   }
}

contracts/NFTRarityRegister/NFTRarityReg.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "./INFTRarityRegister.sol";
import "../AccessControl/RarityRegisterAdminAccessControl.sol";

/// @title Registry holding the rarity value of a given NFT.
/// @author Nemitari Ajienka @najienka
contract NFTRarityRegister is INFTRarityRegister, RarityRegisterAdminAccessControl {
	mapping(address => mapping(uint256 => uint16)) private rarityRegister;

	constructor() RarityRegisterAdminAccessControl(msg.sender, address(this)) {}

	/**
	 * @dev Store the rarity of a given NFT
	 * @param tokenAddress The NFT smart contract address e.g., ERC-721 standard contract
	 * @param tokenId The NFT's unique token id
	 * @param rarityValue The rarity of a given NFT address and id unique combination
	 * using percentage i.e., 100% = 1000 to correct for precision and
	 * to save gas required when converting from category, e.g.,
	 * high, medium, low to percentage in staking contract
	 * can apply rarityValue on interests directly after fetching
	 */
	function storeNftRarity(address tokenAddress, uint tokenId, uint16 rarityValue) external override onlyRarityManager {
		// check tokenAddress, tokenId and rarityValue are valid
		// _exists ERC721 function is internal
		require(tokenAddress != address(0), "NFTRarityRegister: Token address is invalid");
		require(getNftRarity(tokenAddress, tokenId) == 0, "NFTRarityRegister: Rarity already set for token");
		require(rarityValue >= 100, "NFTRarityRegister: Value must be at least 100");

		rarityRegister[tokenAddress][tokenId] = rarityValue;

		emit NftRarityStored(tokenAddress, tokenId, rarityValue);
	}

	/**
	 * @dev Get the rarity of a given NFT
	 * @param tokenAddress The NFT smart contract address e.g., ERC-721 standard contract
	 * @param tokenId The NFT's unique token id
	 * @return The the rarity of a given NFT address and id unique combination and timestamp
	 */
	function getNftRarity(address tokenAddress, uint256 tokenId) public override view returns (uint16) {
		return rarityRegister[tokenAddress][tokenId];
	}
}

