    string memory symbol,
    uint256 initialSupply,
    address owner
  ) public ERC20PresetMinterPauser(name, symbol) { }

  function mintReward(address to, uint256 amount) public {
    _mint(to, amount);
  }
}","keccak256":"0x1ce380a721601aa62e007dfa9b61c9aed1a3bde98e4e21b636fc2249121b619b","license":"MIT"},"contracts/Minepunks.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
