/// @title A minable NFT based on PoW
/// @author Valerio Leo @valerioHQ
contract Minepunks is ERC721 {
  using Counters for Counters.Counter;
  Counters.Counter private _tokenIds;
  string public baseSvg = 'https://ipfs.io/ipfs/Qmb4pJ863umSXcTkYkNxdsoLVEynwUysgNpF4CzxnMqhkx';

  struct TokenMetadata {
    uint16 difficulty;
    address firstOwner;
  }

  struct Accessory {
    MinepunksAccessory accessory;
    uint256 tokenId;
  }

  mapping(uint256 => TokenMetadata) public tokenMetadata;
  mapping(uint256 => Accessory[]) public tokenAccesories;
  address[] public Accessories;

  uint256 public currentSalt;

  StakingV2 public staking;

  event DifficultyRaised(uint256 tokenId, uint16 newLevel);

  constructor (
    StakingV2 _staking
  ) ERC721("Minepunks", "MNPKS") {
    // here we store the initial salt;
    currentSalt = block.timestamp;
    staking = _staking;
  }

  // hash = hash(address, currentSalt, userNonce)
  function getHashFromNonce(uint256 nonce) public view returns (bytes32) {
    bytes32 hash = keccak256(abi.encodePacked(msg.sender, nonce));

    return hash;
  }

  function setStaking(StakingV2 _staking) public {
    staking = _staking;
  }

  function uint2hexstr(uint i) private pure returns (string memory) {
    uint length = 64;
    uint mask = 15;

    bytes memory bstr = new bytes(length);
    uint k = length;
    for (uint256 index = 0; index < length; index++) {
      uint curr = (i & mask);
      bstr[--k] = curr > 9 ?
        bytes1(uint8(55 + curr)) :
        bytes1(uint8(48 + curr)); // 55 = 65 - 10
      i = i >> 4;
    }

    return string(bstr);
  }

  function bytes32ToString(bytes32 _bytes32) public view returns (string memory) {
    return uint2hexstr(uint256(_bytes32));
  }

  function getSubstring(string memory str, uint startIndex, uint endIndex) pure internal returns (string memory) {
    bytes memory strBytes = bytes(str);
    bytes memory result = new bytes(endIndex-startIndex);
    for(uint i = startIndex; i < endIndex; i++) {
      result[i-startIndex] = strBytes[i];
    }
    return string(result);
  }

  function hasZeroAtIndex(bytes32 hash, uint256 index) public view returns(bool) {
    string memory hashString = bytes32ToString(hash);

    string memory char = getSubstring(hashString, index, index + 1);

    bool hasZero = keccak256(abi.encodePacked(char)) == keccak256(abi.encodePacked("0"));

    return hasZero;
  }

  function getNumbersOfZerosFromHash(bytes32 hash) public view returns (uint16) {
    uint16 i;

    while (hasZeroAtIndex(hash, i)) {
      i++;
    }

    return i;
  }

  function getNumbersOfZeros(uint256 nonce) public view returns (uint16) {
    bytes32 hash = getHashFromNonce(nonce);

    return getNumbersOfZerosFromHash(hash);
  }

  function _mintNext() private {
    uint256 tokenId = _tokenIds.current();
    _tokenIds.increment();

    super._mint(msg.sender, tokenId);

    tokenMetadata[tokenId] = TokenMetadata({
      difficulty: 0,
      firstOwner: msg.sender
    });
  }

  function mintWithNonce(uint256 nonce) external {
    uint256 tokenId = _tokenIds.current();
    _tokenIds.increment();

    super._mint(msg.sender, tokenId);

    tokenMetadata[tokenId] = TokenMetadata({
      difficulty: getNumbersOfZeros(nonce),
      firstOwner: msg.sender
    });
  }

  function getHashFromTokenIdAndNonce(uint256 tokenId, uint256 nonce) public view returns (bytes32) {
    uint256 currentTokenDifficulty = tokenMetadata[tokenId].difficulty;

    bytes32 hash = keccak256(abi.encodePacked(msg.sender, tokenId, currentTokenDifficulty, nonce));

    return hash;
  }

  function mint() public {
    _mintNext();
  }

  function raiseLevelWithNonce(uint256 tokenId, uint256 nonce) public {
    // check msg.sender is owner
    require(ownerOf(tokenId) == address(msg.sender), "Only owner can raise level");

    // check that the nonce produces a level higher than current
    bytes32 hashFromNonce = getHashFromTokenIdAndNonce(tokenId, nonce);
    uint16 difficultyFromNonce = getNumbersOfZerosFromHash(hashFromNonce);

    uint256 currentTokenDifficulty = tokenMetadata[tokenId].difficulty;

    uint256 difficultyDelta = difficultyFromNonce - currentTokenDifficulty;

    require(difficultyDelta == 1, "New difficulty must be one level higher than previous");

    // here we update the staking contract
    raiseLevel(tokenId);

    // emit a LevelRaised Event
    emit DifficultyRaised(tokenId, difficultyFromNonce);
  }

  function raiseLevel(uint256 tokenId) public {
    uint256 currentLevel = tokenMetadata[tokenId].difficulty;


    currentLevel == 0
      ? staking.deposit(tokenId)
      : staking.compound(tokenId, currentLevel + 1);

    // here we update the token metadata
    tokenMetadata[tokenId].difficulty += 1;
  }

  function totalDifficulty() public view returns (uint256) {
    return staking.getTotalStakingUnits();
  }

  function uint2str(uint _i) internal pure returns (string memory _uintAsString) {
		if (_i == 0) {
			return "0";
		}
		uint j = _i;
		uint len;

		while (j != 0) {
			len++;
			j /= 10;
		}

		bytes memory bstr = new bytes(len);
		uint k = len;

		while (_i != 0) {
			k = k-1;
			uint8 temp = (48 + uint8(_i - _i / 10 * 10));
			bytes1 b1 = bytes1(temp);
			bstr[k] = b1;
			_i /= 10;
		}

		return string(bstr);
	}

  function tokenURI(uint256 tokenId) public view virtual override returns(string memory) {
    string memory image = Base64.encode(bytes(generateSvg(tokenId)));
    string memory traits = getTraits(tokenId);

    // return string(abi.encodePacked(
    //   'data:application/json;base64,',
    //   Base64.encode(
    //     bytes(abi.encodePacked(
    //       '{',
    //         '"name":"Minepunk ', uint2str(tokenId),'",',
    //         '"description":"This is the description",',
    //         '"animation_url": "data:image/svg+xml;base64,',image,'",',
    //         '"image_data": "data:image/svg+xml;base64,',image,'",',
    //         '"attributes": ', traits,
    //       '}'
    //     ))
    //   )
    // ));

    return 'https://minepunks.web.app/metadata.json';
  }

  function getSvgLayers (uint256 tokenId) private view returns (string memory) {
    bytes memory layers = abi.encodePacked(
      '<image href="',baseSvg,'" height="600" width="800"/>'
    );

    for(uint i = 0 ; i < tokenAccesories[tokenId].length ; i++) {
      Accessory memory accessory = tokenAccesories[tokenId][i];
      uint256 tokenId = accessory.tokenId;
      string memory uri = accessory.accessory.getTokenAbsoluteURI(tokenId);

      layers = abi.encodePacked(
        layers,
        '<image href="https://ipfs.io/ipfs/',uri,'.svg" height="600" width="800"/>'
      );
    }

    return string(layers);
  }

  function getTraits (uint256 tokenId) private view returns (string memory) {
    bytes memory layers = abi.encodePacked('[');

    for(uint i = 0 ; i < tokenAccesories[tokenId].length ; i++) {
      Accessory memory accessory = tokenAccesories[tokenId][i];
      string memory traitType = accessory.accessory.accessoryTraitType(accessory.tokenId);
      string memory traitValue = accessory.accessory.accessoryTraitValue(accessory.tokenId);

      bool isLast = i == tokenAccesories[tokenId].length - 1;

      layers = abi.encodePacked(
        layers,
        '{"trait_type":"',traitType,'","value":"',traitValue,'"}',
        isLast ? '' : ','
      );
    }

    layers = abi.encodePacked(layers, ']');

    return string(layers);
  }

  function generateSvg(uint256 tokenId) public view returns (string memory) {
    string memory svg = string(abi.encodePacked(
      '<svg width="800" height="600" xmlns="http://www.w3.org/2000/svg">',
        '<g>',
          '<title>Layer 1</title>',
          '<ellipse id="svg_3" cy="546" cx="562" stroke="#000" fill="#FFA9A2"/>',
          '<ellipse ry="72" rx="70" id="svg_7" cy="116.5" cx="697" stroke="#000" fill="#E6C36A"/>',
          getSvgLayers(tokenId),
        '</g>',
      '</svg>'
    ));

    return svg;
  }

  function updateAccessory (
    uint256 tokenId,
    MinepunksAccessory accessoryAddress,
    uint256 accessoryId
  ) public {
    tokenAccesories[tokenId].push(Accessory({
      accessory: accessoryAddress,
      tokenId: accessoryId
    }));
  }
}
