/// @title A minable NFT based on PoW
/// @author Valerio Leo @valerioHQ
contract MinepunksAccessory is ERC1155 {

  string public accessoryType = '';
  string public baseUri;

  mapping(uint256 => string) public accessoryTraitType;
  mapping(uint256 => string) public accessoryTraitValue;

	constructor (string memory uri_) ERC1155(uri_) {

	}

	function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155) returns (bool) {
		return super.supportsInterface(interfaceId);
	}

  function uint2str(uint _i) internal pure returns (string memory _uintAsString) {
		if (_i == 0) {
			return "0";
		}
		uint j = _i;
		uint len;

		while (j != 0) {
			len++;
			j /= 10;
		}

		bytes memory bstr = new bytes(len);
		uint k = len;

		while (_i != 0) {
			k = k-1;
			uint8 temp = (48 + uint8(_i - _i / 10 * 10));
			bytes1 b1 = bytes1(temp);
			bstr[k] = b1;
			_i /= 10;
		}

		return string(bstr);
	}

	function tokenURI(uint256 tokenId) public view returns (string memory) {
		string memory resource = super.uri(tokenId);

		return bytes(resource).length > 0 ? string(abi.encodePacked(resource, uint2str(tokenId), ".json")) : "";
	}

	function uri(uint256 tokenId) public view override returns (string memory) {
		string memory resource = super.uri(tokenId);

		return bytes(resource).length > 0 ? string(abi.encodePacked(resource, uint2str(tokenId), ".json")) : "";
	}

  function getTokenAbsoluteURI(uint256 tokenId) public view returns (string memory) {
    string memory resource = super.uri(tokenId);

		return string(abi.encodePacked(resource, '/', uint2str(tokenId)));
  }

	function setTrait(uint256 tokenId, string memory traitType, string memory traitValue) public {
		accessoryTraitType[tokenId] = traitType;
		accessoryTraitValue[tokenId] = traitValue;
	}

  function setTokenData(
    uint256 tokenId,
    string memory _accessoryTraitType,
    string memory _accessoryTraitValue
  ) public {
    accessoryTraitType[tokenId] = _accessoryTraitType;
    accessoryTraitValue[tokenId] = _accessoryTraitValue;
  }
}
