import "@openzeppelin/contracts-upgradeable/utils/math/MathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "../NFTRarityRegister/INFTRarityRegister.sol";
import "../TokenHelper.sol";
import "./RewardVault.sol";
import "./StakingTypes.sol";
import "./StakingLibV2Base.sol";
import "hardhat/console.sol";

library StakingLibV2Core {
  using SafeERC20Upgradeable for IERC20Upgradeable;

  uint256 private constant IDENTITY_MULTIPLIER = 100;

  function claimTickets(
    StakingTypes.StakeInfo storage stake,
    address ticket
  ) public returns(uint256) {

    if(stake.stakingUnits == 0||
      stake.ticketInfo.ticketsMintingRatioWhenEntered == 0 ||
      stake.ticketInfo.ticketsMintingCooldownPeriodRatioWhenEntered == 0
  ) {
      return 0;
    }

    uint256 enteredAtTs = stake.enteredAtTs;
    uint256 lockedTillTs = stake.lockedTillTs;

    uint256 secondsDelta = MathUpgradeable.min(
      (uint256(block.timestamp) - enteredAtTs),
      (lockedTillTs - enteredAtTs)
    ) + stake.ticketInfo.ticketsMintingCooldownPeriodRatioWhenEntered; // count as passed from day 0
    uint256 periodsPassed = secondsDelta / stake.ticketInfo.ticketsMintingCooldownPeriodRatioWhenEntered;
    // 1. multiply tickets
    uint256 multipliedUnits = stake.stakingUnits * periodsPassed;
    // 1. get printable tickets
    uint256 printableTickets = multipliedUnits / stake.ticketInfo.ticketsMintingRatioWhenEntered;
    // 2. subtract any previously minted
    uint256 netPrintableTickets = printableTickets - stake.ticketInfo.ticketsClaimedForCurrentStake;
    uint256 withPending = netPrintableTickets + stake.ticketInfo.pendingTickets;

    TokenHelper._mintTickets(ticket, msg.sender, withPending);
    stake.ticketInfo.ticketsClaimedForCurrentStake = stake.ticketInfo.ticketsClaimedForCurrentStake + netPrintableTickets;
    stake.ticketInfo.pendingTickets = 0;

    return withPending;
  }

  function addNftToStake(
    StakingTypes.StakeInfo storage stake,
    address tokenAddress,
    uint256 tokenId,
    StakingTypes.PoolInfo storage poolInfo
  ) public {
    uint256 rewardMultiplier = 100;

    require(stake.stakingUnits > 0, 'Staking; No stake found');
    require(rewardMultiplier >= 100, 'Staking: NFT multiplier must be at least 100');
    require(stake.stakedNftAddress == address(0), 'Staking: Stake already has a token');

    bool success = TokenHelper.transferNft(tokenAddress, tokenId, 1, '', msg.sender, address(this));
    require(success, "Staking: could not add NFT to stake");

    addNft(
      stake,
      tokenAddress,
      tokenId,
      poolInfo,
      rewardMultiplier
    );
  }

  function addNft(
    StakingTypes.StakeInfo storage stake,
    address tokenAddress,
    uint256 tokenId,
    StakingTypes.PoolInfo storage poolInfo,
    uint256 nftMultiplier
  ) private {
    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );
    uint256 pending = StakingLibV2Base.getPendingForStake(stake, poolInfo);

    if(pending > 0) {
      poolInfo.rewardVault.sendReward(address(this), pending);

      // If the user got rewards we set the cursor to the current global cursor and the reward debt to 0
      // so that we will not perform the same calculations again for past periods
      stake.cursor = poolInfo.globalPeriodCursor;

      // This will be calculated again below using the new user cursor we just set
      stake.rewardDebt = 0;
      // This was included in the total pending amount
      stake.pendingRewards = 0;
    }

    StakingLibV2Base.updateStake(
      stake,
      poolInfo,
      stake.amount + pending,
      StakingLibV2Base.getMultiplierForLock(stake.currentLockDurationIndex, poolInfo.lockPeriodMultipliers),
      nftMultiplier,
      stake.currentLockDurationIndex, // we keep this value the same
      stake.lockedTillTs // we keep this value the same
    );
    stake.stakedNftAddress = tokenAddress;
    stake.stakedNftId = tokenId;
  }

  function withdraw(
    StakingTypes.StakeInfo storage stake,
    uint256 tokenId,
    StakingTypes.PoolInfo storage poolInfo
  ) public returns (uint256 unstakeAmount) {
    require(stake.lockedTillTs <= block.timestamp, "stake locked");

    address tokenOwner = TokenHelper.ownerOf(address(poolInfo.minepunks), tokenId);

    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );
    StakingLibV2Base.releasePending(
      stake,
      tokenOwner,
      poolInfo
    );

    unstakeAmount = stake.amount;
    // poolInfo.token.safeTransfer(address(msg.sender), stake.amount);

    StakingLibV2Base.updateStakeDebt(
      stake,
      poolInfo.rewardPeriods
    );

    // poolInfo.totalStakingUnits -= stake.stakingUnits;
    // stake.amount = 0;
    // stake.stakingUnits = 0;

    if(stake.stakedNftAddress != address(0)) {
      bool success = TokenHelper.transferNft(stake.stakedNftAddress, stake.stakedNftId, 1, '', address(this), msg.sender);
      require(success, "Staking: could not withdraw NFT");
    }
  }

  function unstakeNft(StakingTypes.StakeInfo storage stake) private {
    bool success = TokenHelper.transferNft(stake.stakedNftAddress, stake.stakedNftId, 1, '', address(this), msg.sender);
    require(success, "Staking: could not withdraw NFT");

    stake.stakedNftId = 0;
    stake.stakedNftAddress = address(0);
  }

  function moveStakedNft(
    StakingTypes.PoolInfo storage poolInfo,
    StakingTypes.StakeInfo storage stake,
    StakingTypes.StakeInfo storage stakeNext
  ) private {
    uint256 multipler = 100;
    StakingLibV2Base.updateStakeStakingUnits(stakeNext, StakingLibV2Base.applyPercent(stakeNext.stakingUnits, multipler), poolInfo);
    stakeNext.stakedNftAddress = stake.stakedNftAddress;
    stakeNext.stakedNftId = stake.stakedNftId;
    stake.stakedNftId = 0;
    stake.stakedNftAddress = address(0);
  }

  function handleNftRestake(
    StakingTypes.PoolInfo storage poolInfo,
    StakingTypes.StakeInfo storage stake,
    StakingTypes.StakeInfo storage stakeNext,
    address tokenAddress,
    uint256 tokenId,
    bool removeNftFromStake
  ) private {
    if(removeNftFromStake) {
      unstakeNft(stake);
    }
    else if(stake.stakedNftAddress != address(0) && tokenAddress == address(0) && tokenId == 0) {
      moveStakedNft(poolInfo, stake, stakeNext);
    }
    else if(stake.stakedNftAddress != address(0) && tokenAddress != address(0) && tokenId != 0) {
      uint256 multipler = 100;
      StakingLibV2Base.updateStakeStakingUnits(stakeNext, StakingLibV2Base.applyPercent(stakeNext.stakingUnits, multipler), poolInfo);
      stakeNext.stakedNftAddress = tokenAddress;
      stakeNext.stakedNftId = tokenId;
      bool success = TokenHelper.transferNft(tokenAddress, tokenId, 1, '', msg.sender, address(this));
      require(success, "Staking: could not stake NFT");

      unstakeNft(stake);
    }
    else if(stake.stakedNftAddress == address(0) && tokenAddress != address(0) && tokenId != 0) {
      uint256 multipler = 100;
      StakingLibV2Base.updateStakeStakingUnits(stakeNext, StakingLibV2Base.applyPercent(stakeNext.stakingUnits, multipler), poolInfo);
      stakeNext.stakedNftAddress = tokenAddress;
      stakeNext.stakedNftId = tokenId;
    }
  }

  function updateNewStakeData(
    StakingTypes.PoolInfo storage poolInfo,
    StakingTypes.StakeInfo storage stakeNext,
    uint256 amount,
    uint256 lockIndex,
    uint256 lockMultiplier,
    uint256 lockedTillTs
  ) public {
    stakeNext.amount = amount;
    StakingLibV2Base.updateStakeStakingUnits(stakeNext, StakingLibV2Base.applyPercent(stakeNext.amount, lockMultiplier), poolInfo);
    stakeNext.currentLockDurationIndex = lockIndex;
    stakeNext.lockedTillTs = lockedTillTs;
    stakeNext.enteredAtTs = block.timestamp;
  }

  function reStake(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.StakeInfo storage stakeNext,
    StakingTypes.PoolInfo storage poolInfo,
    uint256 amount,
    uint256 lockIndex,
    address tokenAddress,
    uint256 tokenId,
    bool removeNftFromStake
  ) public {
    uint256 lockMultiplier = StakingLibV2Base.getMultiplierForLock(lockIndex, poolInfo.lockPeriodMultipliers);
    uint256 lockDurationSeconds = StakingLibV2Base.getLockDurationInSeconds(lockIndex, poolInfo.lockPeriods);
    uint256 lockedTillTs = block.timestamp + lockDurationSeconds;
    require(stake.lockedTillTs <= block.timestamp, 'stake locked');
    require(stake.amount > 0, 'cannot restake without an existing stake');
    require(amount > 0, 'cannot restake 0');

    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );

    uint256 pending;
    if (stake.stakingUnits > 0) {
      pending = StakingLibV2Base.getPendingForStake(stake, poolInfo);

      if(pending > 0) {
        // move the rewards into the contract until we calculate the final restake amount
        poolInfo.rewardVault.sendReward(address(this), pending);

        // If the stake got rewards we set the cursor to the current global cursor and the reward debt to 0
        // so that we will not perform the same calculations again for past periods
        stake.cursor = poolInfo.globalPeriodCursor;

        // This will be calculated again below using the new stake cursor we just set
        stake.rewardDebt = 0;
        // This was included in the total pending amount
        stake.pendingRewards = 0;
      }
    }
    uint256 maxRestakeAmount = stake.amount + pending;
    require(amount <= maxRestakeAmount, 'cannot restake more than the staked amount plus pending rewards');

    uint256 remainder = maxRestakeAmount - amount;
    if(remainder > 0) {
      poolInfo.token.safeTransfer(msg.sender, remainder);
    }

    poolInfo.totalStakingUnits -= stake.stakingUnits;
    stake.stakingUnits = 0;
    stake.amount = 0;

    updateNewStakeData(
      poolInfo,
      stakeNext,
      amount,
      lockIndex,
      lockMultiplier,
      lockedTillTs
    );

    handleNftRestake(
      poolInfo,
      stake,
      stakeNext,
      tokenAddress,
      tokenId,
      removeNftFromStake
    );

    StakingLibV2Base.updateStakeDebt(stake, poolInfo.rewardPeriods);
  }

  // function _compounRewards(
  //   StakingTypes.StakeInfo storage stake,
  //   StakingTypes.PoolInfo storage poolInfo
  // ) private returns(uint256 pending) {
  //   if (stake.stakingUnits > 0) {
  //     pending = StakingLibV2Base.getPendingForStake(stake, poolInfo);

  //     if(pending > 0) {
  //       poolInfo.rewardVault.sendReward(address(this), pending);

  //       // If the user got rewards we set the cursor to the current global cursor and the reward debt to 0
  //       // so that we will not perform the same calculations again for past periods
  //       stake.cursor = poolInfo.globalPeriodCursor;

  //       // This will be calculated again below using the new user cursor we just set
  //       stake.rewardDebt = 0;
  //       // This was included in the total pending amount
  //       stake.pendingRewards = 0;
  //     }
  //   }
  // }

  function compound(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.PoolInfo storage poolInfo,
    uint256 newLevel
  ) public returns(uint256 pending) {
    console.log("stake: ", stake.enteredAtTs);

    pending = StakingLibV2Base.pendingReward(stake, poolInfo);

    uint256 frontenPending = StakingLibV2Base.pendingReward(stake, poolInfo);
    console.log("frontenPending: ", frontenPending);

    uint256 lockMultiplier = 100;
    uint256 nftMultiplier = 100;

    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );

    console.log("stake.pendingRewards: ", stake.pendingRewards);
    console.log("pending: ", pending);

    stake.pendingRewards += pending;
    console.log("stake.pendingRewards: ", stake.pendingRewards);

    StakingLibV2Base.updateStake(
      stake,
      poolInfo,
      newLevel,
      lockMultiplier,
      nftMultiplier,
      stake.currentLockDurationIndex, // we keep this value the same
      stake.lockedTillTs // we keep this value the same
    );
  }

  function deposit(
    uint256 amount,
    uint256 lockIndex,
    StakingTypes.StakeInfo storage stake,
    StakingTypes.PoolInfo storage poolInfo
  ) public returns (uint256) {
    uint256 lockMultiplier = 100; // StakingLibV2Base.getMultiplierForLock(lockIndex, poolInfo.lockPeriodMultipliers);
    uint256 lockDurationTs = 0; // StakingLibV2Base.getLockDurationInSeconds(lockIndex, poolInfo.lockPeriods);

    StakingLibV2Base.updatePool(poolInfo);
    StakingLibV2Base.updateStakeCursor(
      stake,
      poolInfo
    );

    stake.enteredAtTs = block.timestamp;

    StakingLibV2Base.updateStake(
      stake,
      poolInfo,
      stake.amount + amount,
      lockMultiplier,
      IDENTITY_MULTIPLIER,
      lockIndex,
      block.timestamp + lockDurationTs
    );

    uint256 printedTickets = 0;

    return printedTickets;
  }

  function emergencyWithdraw(
    StakingTypes.StakeInfo storage stake,
    StakingTypes.PoolInfo memory poolInfo
  ) public {
    poolInfo.token.safeTransfer(address(msg.sender), stake.amount);
    poolInfo.totalStakingUnits -= stake.stakingUnits;
    stake.amount = 0;
    stake.stakingUnits = 0;
    stake.rewardDebt = 0;
    stake.pendingRewards = 0;
  }
