import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "./RewardVault.sol";
import "../Minepunks.sol";

contract StakingTypes {
  // Info of each user.
  struct StakeInfo {
    uint256 amount; // The total amount the user has staked
    uint256 stakingUnits; // The staking units the user has after applying multipliers
    uint256 rewardDebt;
    uint256 enteredAtTs; // The ts where the current stake started for the user
    uint256 lastTicketClaimTs; // The last ts at which the user claimed tickets
    uint256 currentLockDurationIndex; // The duration in seconds for the current stake
    uint256 lockedTillTs; // The second until which the stake is locked
    address stakedNftAddress;
    uint256 stakedNftId;
    uint256 pendingRewards; // Pending rewards for the user
    uint256 cursor;
    TicketInfo ticketInfo;
  }

  // Info for a reward period
  struct RewardPeriodInfo {
    uint256 startTs; // When the period starts
    uint256 endTs; // When the period ends
    uint256 lastRewardTs;  // Last ts that tokens distribution occurs.
    uint256 rewardPerSecond; // How much rewards are distributed per second
    uint256 accRewardPerShare; // Accumulated Rewards per share, times 1e18
    uint256 endAccRewardPerShare; // Accumulated Rewards per share on period end, times 1e18. Used to avoid recalculating for periods that have ended
  }

  struct TicketInfo {
    uint256 ticketsMintingRatioWhenEntered;
    uint256 ticketsMintingCooldownPeriodRatioWhenEntered;
    uint256 ticketsClaimedForCurrentStake; // How mant tickets the user has claimed for the current stake
    uint256 pendingTickets; // Pending tickets for the user
  }

  struct PoolInfo {
    uint256 globalPeriodCursor;
    uint256 totalStakingUnits;
    uint256 lastRewardTs;
    uint256 ticketsMintingRatio;
    uint256 ticketsMintingCooldownPeriod;
    uint256[] lockPeriods;
    uint256[] lockPeriodMultipliers;
    RewardPeriodInfo[] rewardPeriods;
    RewardVault rewardVault;
    IERC20Upgradeable token;
    Minepunks minepunks;
  }
}","keccak256":"0x91d1df5f590c2c342b911f7ac8af8cac051ad3edf5ec9022bf18bc7e8348a253"},"contracts/Staking/StakingV2.sol":{"content":"pragma solidity 0.8.10;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/IERC1155ReceiverUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "../AccessControl/StakingAccessControl.sol";
import "../NFTRarityRegister/INFTRarityRegister.sol";
import "./RewardVault.sol";
import "./StakingTypes.sol";
import "./StakingLibV2Core.sol";
import "./StakingLibV2Base.sol";
import "../Minepunks.sol";

import "hardhat/console.sol";

contract StakingV2 is Initializable, IERC1155ReceiverUpgradeable, StakingAccessControl, ReentrancyGuardUpgradeable {
  using SafeERC20Upgradeable for IERC20Upgradeable;

  event StakeCreated(address indexed user, uint256 amount, uint256 stakeIndex);
  event StakeWithdrawn(address indexed user, uint256 amount, uint256 stakeIndex, bool isEmergencyWithdrawal);
  event StakeCompounded(address indexed user, uint256 amount, uint256 stakeIndex);
  event TicketsClaimed(address indexed user, uint256 amount, uint256 stakeIndex);
  event NftAdded(address indexed user, uint256 tokenId, address tokenAddress, uint256 stakeIndex);
  event ReStaked(address indexed user, uint256 amount, uint256 fromStakeIndex, uint256 newStakeIndex);

  StakingTypes.PoolInfo public poolInfo;

  mapping (uint256 => StakingTypes.StakeInfo) public stakeInfo;
  bool public emergencyWithdrawEnabled;

  Minepunks public minepunks;

  // modifier validStake(address user, uint256 stakeIndex) {
  //   require(stakeInfo[user].length > stakeIndex, 'Staking: No stake found');
  //   _;
  // }

  modifier canEmergencyWithdraw() {
    require(emergencyWithdrawEnabled, 'Staking: emergency withdraw is disabled');
    _;
  }

  /**
   * Constructor
   * @param token The reward/staking token address
   * @param _endTs The timestamp that the last period will end
   * @param _ticketsMintingRatio Minting ration for the raffle tickets
   * @param _ticketsMintingCooldownPeriod Cooldown period for ticket minting
   * @param periodsStartTs Array of timestamps for when each period will start.
   * Each period will end 1 sec before the next starts and the last period will end at _endTs
   * @param periodsRewardsPerSecond Rewards per second for each period. 1-1 with periodsStartTs
   * @param _rewardVault The address of the reward vault that holds the reward tokens
   */
  function initialize(
    IERC20Upgradeable token,
    uint256 _endTs,
    uint256 _ticketsMintingRatio,
    uint256 _ticketsMintingCooldownPeriod,
    uint256[] memory periodsStartTs,
    uint256[] memory periodsRewardsPerSecond,
    RewardVault _rewardVault,
    Minepunks _minepunks
  ) public initializer {
    StakingAccessControl.__StakingAccessControl_init(msg.sender);

    StakingLibV2Base.init(
      token,
      _endTs,
      _ticketsMintingRatio,
      _ticketsMintingCooldownPeriod,
      periodsStartTs,
      periodsRewardsPerSecond,
      _rewardVault,
      _minepunks,
      poolInfo
    );
  }

  function getTotalStakingUnits() public view returns (uint256) {
    return poolInfo.totalStakingUnits;
  }

  function onERC1155BatchReceived(address, address, uint256[] memory, uint256[] memory, bytes calldata) public virtual override returns (bytes4) {
    return this.onERC1155Received.selector;
  }

  function onERC1155Received(address, address, uint256, uint256, bytes calldata) public virtual override returns (bytes4) {
    return this.onERC1155Received.selector;
  }

  // ERC165 interface support
  function supportsInterface(bytes4 interfaceID) public pure override(IERC165Upgradeable, AccessControlEnumerableUpgradeable) returns (bool) {
    return  interfaceID == 0x01ffc9a7 ||    // ERC165
            interfaceID == 0x4e2312e0;      // ERC1155_ACCEPTED ^ ERC1155_BATCH_ACCEPTED;
  }

  function rewardPeriods(uint256 index) external view returns (StakingTypes.RewardPeriodInfo memory) {
    return poolInfo.rewardPeriods[index];
  }

  // function userStakes(address user) external view returns(uint256) {
  //   return stakeInfo[user].length;
  // }

  /**
   * @notice Used to get the pending rewards for a user at the given time.
   * Will do all the calculation for the reward per share so it will always return up to date data
   * @param stakeIndex The stake for which we want the pending rewards
   */
  function pendingReward(uint256 stakeIndex) external view returns (uint256) {
    StakingTypes.StakeInfo storage stake = stakeInfo[stakeIndex];

    return StakingLibV2Base.pendingReward(
      stake,
      poolInfo
    );
  }

  // function setTicket(address _ticket) external onlyManager {
  //   poolInfo.ticket = _ticket;
  // }

  function updateTickerMintingConfig(
    uint256 ticketsMintingRatio,
    uint256 ticketsMintingCooldownPeriod
  ) public onlyManager {
    poolInfo.ticketsMintingRatio = ticketsMintingRatio;
    poolInfo.ticketsMintingCooldownPeriod = ticketsMintingCooldownPeriod;
  }

  function updateContracts(
    IERC20Upgradeable _token,
    RewardVault _rewardVault,
    Minepunks _minepunks
  ) public onlyManager {
    poolInfo.token = _token;
    poolInfo.rewardVault = _rewardVault;
    poolInfo.minepunks = _minepunks;
  }

  function toggleEmergencyWithdraw() external {
    emergencyWithdrawEnabled = !emergencyWithdrawEnabled;
  }

  /**
   * @notice Will add a new period at the end of the periods array
   * @param endTs The timestamp at which the period will end in seconds
   * @param periodRewardsPerSecond The rewards per second that will be distributed by the period
   */
  function addPeriod(
    uint256 endTs,
    uint256 periodRewardsPerSecond
  ) external onlyManager {
    StakingLibV2Base.addPeriod(
      poolInfo,
      endTs,
      periodRewardsPerSecond
    );
  }

  /**
   * @notice Will update an existing period by splitting it into 3 new periods or 2 if it is the last period
   * @param startTs The timestamp in seconds where the period will start
   * @param endTs The timestamp in seconds where the period will end
   * @param periodRewardsPerSecond The rewards per second that will be distributed by the period
   */
  function updatePeriod(
    uint256 startTs,
    uint256 endTs,
    uint256 periodRewardsPerSecond
  ) external onlyManager {
    StakingLibV2Base.updatePeriod(
      startTs,
      endTs,
      periodRewardsPerSecond,
      poolInfo
    );
  }

  function getUserStake(uint256 stakeIndex) public view returns(StakingTypes.StakeInfo memory) {
    return stakeInfo[stakeIndex];
  }

  /**
   * @notice Will update all the lock periods and their multipliers. All the existing periods will be ovewritten
   * @param _lockPeriods The new lock periods
   * @param _lockPeriodMultipliers The new multipliers for each lock period
   */
  function updateLockPeriods(
    uint256[] memory _lockPeriods,
    uint256[] memory _lockPeriodMultipliers
  ) external onlyManager {
    poolInfo.lockPeriodMultipliers = _lockPeriodMultipliers;
    poolInfo.lockPeriods = _lockPeriods;
  }

  /**
   * @notice Will depposit tokens from the caller's account to this contract.
   * The user must have previously approved the tokens for this contracts address
   * @param tokenId The tokenId that owns this stake
   */
  function deposit(uint256 tokenId) external nonReentrant {
    uint256 lockIndex = 0;
    uint256 amount = 1;

    StakingTypes.StakeInfo storage stake = stakeInfo[tokenId];

    uint256 printedTickets = StakingLibV2Core.deposit(
      amount,
      lockIndex,
      stake,
      poolInfo
    );

    // emit StakeCreated(msg.sender, amount, newStakeIndex);

    // if(printedTickets > 0) {
