   * @param stakeIndex The stake that will be compounded
   * @param newLevel The new levelcompounded
   */
  function compound(uint256 stakeIndex, uint256 newLevel) external nonReentrant {
    StakingTypes.StakeInfo storage stake = stakeInfo[stakeIndex];

    require(stake.amount < newLevel, "New level must be higher than existing one");

    uint256 amount = StakingLibV2Core.compound(
      stake,
      poolInfo,
      newLevel
    );

    // emit StakeCompounded(msg.sender, amount, stakeIndex);
  }

