   * @param tokenId The id of the token to be staked
   * @param tokenAddress The address of the token contract
   * @param stakeIndex The stake to which the NFT will be added
   */
  function addNftToStake(uint256 stakeIndex, address tokenAddress, uint256 tokenId)
    external
    nonReentrant
  {
    StakingTypes.StakeInfo storage stake = stakeInfo[stakeIndex];

    StakingLibV2Core.addNftToStake(
      stake,
      tokenAddress,
      tokenId,
      poolInfo
    );

    emit NftAdded(msg.sender, tokenId, tokenAddress, stakeIndex);
  }

  /**
   * @notice Will send any available tickets to the user
   * @param stakeIndex The stake from which to claim tickets
   */
  function claimTickets(uint256 stakeIndex) external nonReentrant {
    StakingTypes.StakeInfo storage stake = stakeInfo[stakeIndex];

    uint256 printedTickets = 100;

    emit TicketsClaimed(msg.sender, printedTickets, stakeIndex);
  } 

  /**
   * @notice Will withdraw a stake and send the staked tokens plus any pending rewards to the user and any NFTs in this stake
   * @param tokenId The Stake from which to withdraw
   */
  function withdraw(uint256 tokenId) external nonReentrant {
    StakingTypes.StakeInfo storage stake = stakeInfo[tokenId];

    // uint256 printedTickets = 100;

    uint256 amount = StakingLibV2Core.withdraw(
      stake,
      tokenId,
      poolInfo
    );

    // if(printedTickets > 0) {
    //   emit TicketsClaimed(msg.sender, printedTickets, stakeIndex);
    // }

    // emit StakeWithdrawn(msg.sender, amount, stakeIndex, false);
  }

  /**
   * @notice Used to withdraw the users stake from the contract in the case of an emergency.
   * This will simply return the stake to the user without any rewards
   * @param stakeIndex The stake which to withdraw
   */
  function emergencyWithdraw(uint256 stakeIndex) external canEmergencyWithdraw nonReentrant {
    StakingTypes.StakeInfo storage stake = stakeInfo[stakeIndex];

    StakingLibV2Core.emergencyWithdraw(
      stake,
      poolInfo
    );

    emit StakeWithdrawn(msg.sender, stake.amount, stakeIndex, true);
  }
